import React, { useState } from 'react';



const Button = () => {

    const [count] = useState(5, incrementNumber);

    
    const incrementNumber = () => {
        return count++;
    }

    return(
        <>
            <h1 className="heading"> {count} </h1>
            <button onClick={incrementNumber}> Click me </button>
        </>
    );
}

export default Button;
