import React, { useState } from 'react';
import Events from './Events';
import './index.css';


function App(){

    const bgColor = "green";

    const [bg, setColor] = useState(bgColor);

    const bgChange = () => {
        const newBg = 'red';
        setColor(newBg);
    }


    return(
        <>
            <div style={{backgroundColor:bgColor}}>
                <button onClick={bgChange}> Click Me </button>  
            </div>
        </>
    );
}


export default App;
