    const bgChange = () => {
        let newBG = 'lightblue';
        setBg(newBG);
    }

    return(
        <>
            <div style={{backgroundColor:bg}}>
                <button onClick={bgChange}> Click Me </button>  
            </div>
        </>
    );
}


export default App;
