import React from 'react';

const App = () => {

    const [name, setName] = useState("");

    const inputEvent = (event) => {
        setName(event.target.value);
    }
    
    return(
        <div>
            <h1> Hello {name} </h1>
            <input type="text" placeholder="Enter your name" 
                onChange={inputEvent}
            />
            <button> Submit </button>
        </div>
    );

}

export default App;
