import React, { useState } from "react";
import "./TodoList.css";

const TodoListItem = (props) => {
  return (
    <li>
      <button className="remove-btn"> x </button>
      <span>{props.todolist}</span>
    </li>
  );
};

function TodoList() {
  const [todo, setTodo] = useState();

  const [addTodoList, setAddTodoList] = useState();

  const addTodo = (e) => {
    const value = e.target.value;
    setTodo(value);
  };

  const setTodoList = () => {
    setAddTodoList(addTodoList);
  };

  return (
    <div className="container-div">
      <div className="center-div">
        <h1>Shopping List</h1>
        <div className="add-item">
          <input type="text" placeholder="Add a item" onChange={addTodo} />
          <button className="Add-btn" onClick={setTodoList}></button>
        </div>
        <ol>
          <TodoListItem todolist={todo.addTodo} />
        </ol>
      </div>
    </div>
  );
}

export default TodoList;
