import React, { useState } from 'react';


const time = new Date().toLocaleTimeString();

const Timer = () => {

    const[currentTime, setTimer] = useState(time);

    const setCurrentTime = () => {
        setTimer(time);
    }

    return(
        <>
            <h1> {currentTime} </h1>
            <button onClick={setCurrentTime}> Get Time </button>
        </>
    );

}



const Button = () => {
    return(
        <Timer />
    );
}

export default Button;
