import React, { useState } from 'react';
import Events from './Events';
import './index.css';


function App(){

    let color = 'purple';
    const [bgColor, setBg] = useState(color);
    const [text, changeText] = useState('Click Me');

    const bgChange = () => {
        let newBG = 'lightblue';
        setBg(newBG);
        changeText("OUCH !! MFðŸ˜‚")
    }

    return(
        <>
            <div style={{backgroundColor:bgColor}}>
                <button onClick={bgChange}> {text} </button>  
            </div>
        </>
    );
}


export default App;
