import React, { useState } from 'react';



const Button = () => {

    const newTime = new Date().toLocaleTimeString();

    const [currTime, updateTime] = useState(newTime);

    setInterval(() => {
        newTime = new Date().toLocaleTimeString();
        updateTime(newTime);
    },1000)

    return(
        <h1> {currTime} </h1>
    );
}

export default Button;
