import React from 'react';


let count = 0;

const IncrementNumber = () => {
    count++;
    console.log("clicked");
}

const Button = () => {
    return(
        <>
            <h1 className="heading"> {count} </h1>
            <button onClick={IncrementNumber}> Click me </button>
        </>
    );
}

export default Button;
